import React, { useState } from 'react';
import Navigation from './navigation';
import './checklist.css';

const SecurePage = () => {
    const [modal, setModal] = useState('');
    const [data, setData] = useState([]);

    const openModal = (modalName) => setModal(modalName);
    const closeModal = () => setModal('');

    // Function to fetch data from the server
    const fetchData = async () => {
        try {
            const response = await fetch('http://localhost:3000/scan');
            const result = await response.json();
            setData(result);
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    };

    return (
        <div>
            <Navigation openModal={openModal} fetchData={fetchData} />
            <div className="content">
                <img src="/path-to-your-image.jpg" alt="Company Logo" />
                <h1>Welcome to the Secure Page</h1>
                <p>Click the "Scan" button to display data.</p>

                {/* Only show data when the Scan button is clicked */}
                {data.length > 0 && (
                    <div className="data-box">
                        <h2>Instructions</h2>
                        <ul>
                            {data.map((item, index) => (
                                <li key={index}>
                                    {item["Last Name, First Name (Legal Name)"] || 'Unknown'}
                                </li>
                            ))}
                        </ul>
                    </div>
                )}
            </div>

            {/* Modals for each button */}
            {modal && (
                <div className="modal" onClick={closeModal}>
                    <div className="modal-content">
                        <span className="close" onClick={closeModal}>&times;</span>
                        <h2>{modal} Instructions</h2>
                        <embed src={`/path-to-your-pdfs/${modal}.pdf`} className="pdf-viewer" type="application/pdf" />
                    </div>
                </div>
            )}
        </div>
    );
};

export default SecurePage;
